document.addEventListener("DOMContentLoaded",async()=>{document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll("button[data-audio-id]").forEach(t=>{t.addEventListener("click",()=>{var e=t.getAttribute("data-audio-id"),e=document.getElementById(e);e&&(document.querySelectorAll("audio").forEach(e=>e.pause()),0<e.currentTime&&e.paused&&(e.currentTime=0),e.play())})})}),(async()=>{try{var e=await fetch("src/test.json");if(!e.ok)throw new Error("HTTP error! status: "+e.status);var t=await e.json(),a=document.getElementById("template").innerHTML,r=Handlebars.compile(a)(t);document.getElementById("app").innerHTML=r}catch(e){console.error("Error:",e)}})()});class Validator{static validateInput(e){return!(!e||""===e.trim())}}